const app = {
  service: 'rabbit',
  credentials: {
    username: false,
    password: false
  },
  host: 'localhost',
  vhost: '/',
  awsConfigFile: false,
  MaxNumberOfMessages: 10,
  WaitTimeSeconds: 10
}

app.load = function (opts) {
  Object.keys(opts).forEach(function (key) {
    if (app[key]) app[key] = opts[key]
  })

  return app
}

module.exports = app
